import React, {useState} from "react";
import './index.css';

const App = () =>{

    const [name , setfruits] = useState("");
    const [Items , setItems] = useState([]);

    const onClick = (object) =>{
        setfruits(object.target.value);
    };

    const submit = () =>{
        setItems((oldItems)=>{
            return[...oldItems , name];
        });
        setfruits("");
    }

    const deleted = () =>{
        console.log("deleted");
    }

    return(
    <>
        <div className="main_div">
                <div className="center_div">
                    {/* <h1>ToDo List Example</h1> */}
                    <input type='text' placeholder="Add New Items" className="field" onChange={onClick}
                        value={name}
                    />
                    <button onClick={submit}>Add</button>
                 
                 <ul>
                     {
                         Items.map((itemVal)=>{
                             return(
                             <> 
                                <li>
                                <button className="Delete" onClick={deleted}></button>
                                   {itemVal}             
                                </li>
                              </>
                                );
                             }
                        )
                     }
                 </ul>

                </div>
        </div>
    </>
    )
}; 

export default App;
