import React, {useState} from "react";
// import ModeEditIcon from '@mui/icons-material/ModeEdit';
import ToDoList from './ToDoList';


const App = () => {
    const [inputList , setInputList] = useState("");
    const [Items , setItems] = useState([]);

    const itemEvent = (object) => {
          setInputList(object.target.value);
    };

    const listOfItems = () => {
        if(!inputList){

        }else{
        setItems((oldItems) => {
            return [...oldItems,inputList];
        });   
        setInputList("");
    }
    };

    // const listOfItems = () =>{
    //     if(!inputList){
                
    //     }else{
    //         const allInputList = { id:new Date().getTime().toString() , name:inputList }
    //         setItems([...Items,allInputList]);
    //         setInputList('');
    //     }
    // };

    
    const deleteItems = (id) =>{
        setItems((oldItems)=>{
            return oldItems.filter((arrElem , index) => {
                return index  !== id;
            });
        });
    };

    const DeleteAll = () =>{
        setItems([])
    }
    

    return(
        <>
            <div className="main_div">
              <div className="center_div">
                <br/>
                <h1> ToDoList</h1>
                <br/>
                <input type="text" placeholder="Add a Items" className="field" onChange={itemEvent} value={inputList} />
                <button onClick={listOfItems} className="Add">Add</button>
                <button className="Add" onClick={DeleteAll}>Delete</button>

                <ol>
                   <li>
                   {Items.map((itemVal , index) => {
                     return    <ToDoList
                             key={index}
                             id={index}
                             text={itemVal}
                             onSelect = {deleteItems}
                         />;})}
                         
                    </li>
                </ol>
                
              </div>
            </div>
        </>
    )
    
};
export default App;
