import React, {useState} from "react";
import './index.css';

const App = () =>{

    const [name , setfruits] = useState("");
    const [Items , setItems] = useState([]);

    const onClick = (object) =>{
        setfruits(object.target.value);
    };

    const submit = () =>{
        setItems((oldItems)=>{
            return[...oldItems , name];
        });
        setfruits("");
    }

    return(
    <>
        <div className="main_div">
                <div className="center_div">
                    <h1>ToDo List Example</h1>
                    <input type='text' placeholder="Add New Items" className="field" onChange={onClick}
                        value={name}
                    />
                    <button onClick={submit}>Add</button>
                 
                 <ul>
                     {
                         Items.map((itemVal)=>{
                             return(
                             <> 
                                <li>
                                   <input type='button' value='delete'/>
                                   <img src="https://previews.123rf.com/images/esfirse/esfirse1812/esfirse181200156/115299132-cross-sign-red-x-icon-isolated-on-white-background-circle-symbol.jpg" height='30px'/>
                                   {itemVal}
                                </li>
                              </>
                                );
                             }
                        )
                     }
                 </ul>

                </div>
        </div>
    </>
    )
}; 

export default App;
