import React, {useState} from "react";
import './index.css';
import ToDoList from "./ToDoList";

const App = () =>{

    const [name , setfruits] = useState("");
    const [Items , setItems] = useState([]);

    const onClick = (object) =>{
        setfruits(object.target.value);
    };

    const submit = () =>{
        setItems((oldItems)=>{
            return[...oldItems , name];
        });
        setfruits("");
    }

    const deleted = () =>{
        console.log("deleted");
    }

    return(
    <>
        <div className="main_div">
                <div className="center_div">
                    <h1>ToDo List Example</h1>
                    <input type='text' placeholder="Add New Items" className="field" onChange={onClick}
                        value={name}
                    />
                    <button onClick={submit}>Add</button>
                 
                 <ul>
                     {
                         Items.map((itemVal , index)=>{
                             return(
                             <> 
                                <ToDoList
                                    key={index}
                                    id={index}
                                    text={itemval}

                                />
                              </>
                                );
                             }
                        )
                     }
                 </ul>
                </div>
        </div>
    </>
    )
}; 

export default App;
