import React, { useState , useEffect } from "react";
import todo from "../logo.svg";
import AddCircleIcon from '@mui/icons-material/AddCircle';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';

const getLocalItems = () =>{
    let lists = localStorage.getItem('list');
    console.log(lists);

    if(lists){
        return JSON.parse(localStorage.getItem('list'));
    }else{
        return[];
    }
}

const App = () =>{
    const[inputData , setInputData] = useState('');
    const[Items , setItems] = useState([]);

    const[toggle , settoggle] = useState(flase);

    const addItems = () =>{
        if(!inputData){

        }else{
         setItems([...Items,inputData]);
         setInputData('');
        }
    }

    const deleteItems = (id) =>{
        const updateditems = Items.filter((elem , ind) =>{
            return ind != id;
        });
        setItems(updateditems);
    }

    const deleteAll = () =>{
        setItems([]);
    }

    useEffect(() =>{
          localStorage.setItem('list' , JSON.stringify(Items));
    } ,[Items] )

    const editItems = (id) =>{
        const newEditItems = Items.find((elem)=>{
            return index = id;
        }) ;
        console.log(newEditItems);

        settoggle(false);

        setInputData(newEditItems.name);

    }

     return(
         <>
             <div>
             <div className="main-div">
                <div className="center_div">
                    <figure>
                        <img src={todo} alt="todologo" className="logo"/><br/><br/>
                        <figcaption className="text">Add Your List Here 🤞</figcaption>
                    </figure>

                    <div>
                        <input type='text' placeholder="✍️ Add Items..  " id="" className="field" value={inputData} onChange={(e)=>setInputData(e.target.value)}/>
                        {
                            toggle ? <AddCircleIcon onClick={addItems} /> :<EditIcon className="edit" onClick={() =>editItems(index)}/>
                        }
                        
                    </div>

                    <div className=" ShowItems ">
                    {
                        Items.map((elem , index)=>{
                            return(
                                
                                    <div className="eachItems" key={index}>
                                        <h3>  <DeleteIcon className="delete" onClick={() => deleteItems(index)}/><EditIcon className="edit" onClick={() =>editItems(index)}/>{elem}  </h3>
                                    </div>
                            )
                        })
                    }
                    </div>

                    <div className="ShowItems">
                        <button className="DeleteAll" onClick={deleteAll}>DeleteAll</button>
                    </div>

                </div>
            </div>
             </div>
         </>
     )
}

export default App;
