import React, {useState} from "react";
// import DeleteIcon from '@material-ui/icons/Delete';


const App = () => {
    const [inputList , setInputList] = useState("");
    const [Items , setItems] = useState([]);

    const itemEvent = (object) => {
          setInputList(object.target.value);
    };

    const listOfItems = () => {
        if(!inputList){

        }else{
        setItems((oldItems) => {
            return [...oldItems,inputList];
        });   
        setInputList("");
    }
    };

    
    const deleteItems = (id) =>{
        setItems((oldItems)=>{
            return oldItems.filter((arrElem , index) => {
                return index  !== id;
            });
        });
    };

    
    

    

    return(
        <>
            <div className="main_div">
              <div className="center_div">
                <br/>
                <h1> ToDoList</h1>
                <br/>
                <input type="text" placeholder="Add a Items" className="field" onChange={itemEvent} value={inputList} />
                <button onClick={listOfItems} className="Add">Add</button>

                <ol>
                   <li>
                   {Items.map((itemVal , index) => {
                     {/* return    <ToDoList
                             key={index}
                             id={index}
                             text={itemVal}
                             onSelect = {deleteItems}
                         />; */}
                         return(
                             <div key={index}>
                             {/* <i className="far fa-trash-alt add-btn" title="Delete Item" onClick={deleteItems}></i> */}
                             <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-trash" viewBox="0 0 16 16" onClick={deleteItems}>
                              <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V6z"/>
                             <path fill-rule="evenodd" d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1zM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118zM2.5 3V2h11v1h-11z"/>
                            </svg>
                               {itemVal}  
                             </div>
                         )
                    })}
                    </li>
                </ol>

              </div>
            </div>
        </>
    )
    
};
export default App;
