import React, {useEffect, useState} from "react";
import DeleteIcon from '@material-ui/icons/Delete';


const getLocalItems = () =>{
      let list = localStorage.getItem('lists');
      console.log(list);

      if(list) {
          return JSON.parse(localStorage.getItem('lists'));
      }else{
          return[];
      }
}


const App = () => {
    const [inputList , setInputList] = useState("");
    const [items , setItems] = useState(getLocalItems());

    const itemEvent = (object) => {
          setInputList(object.target.value);
    };

    const listOfItems = () => {
        if(!inputList){

        }else{
            const allInputList = { id: new Date().getTime().toString() , name:inputList }     
            setItems([...items,allInputList]);    //sprate operator
            setInputList("");
    }
    };
    const deleteItems = (id) => {
        // console.log(id);
        const updateitems = items.filter((elem, index) => {
              return index !=  id;
        });
        setItems(updateitems);
    }

    const DeleteAll = () =>{
        setItems([]); 
    }
    
    // useEffect(() =>{
    //     localStorage.setItem('lists' .JSON.stringify(items))
    // },[items]);

    return(
        <>
            <div className="main_div">
              <div className="center_div">
                <br/>
                <h1> ToDoList</h1>
                <br/>
                <input type="text" placeholder="Add a Items" className="field" onChange={itemEvent} value={inputList} />
                <button onClick={listOfItems} className="Add">Add</button>
                <button className="DeleteAll" onClick={DeleteAll}>DeleteAll</button>

                <ol>
                    <li>
                        {
                            items.map((itemVal) =>{
                                return(
                                    <div className="div" key={itemVal.id}>
                                        <button className="Delete" onClick={() => deleteItems(itemVal.id)}><DeleteIcon/></button>
                                        <button className="Edit">E</button>
                                        <h2>{itemVal.name}</h2>
                                    </div>
                                )
                            } )
                        }
                    </li>
                </ol>
                
              </div>
            </div>
        </>
    )
    
};
export default App;




