import React, {useState} from "react";
import ToDoList from './ToDoList';
import DeleteIcon from '@material-ui/icons/Delete';


const App = () => {
    const [inputList , setInputList] = useState("");
    const [Items , setItems] = useState([]);

    const itemEvent = (object) => {
          setInputList(object.target.value);
    };

    const listOfItems = () => {
        if(!inputList){

        }else{
        setItems((oldItems) => {
            return [...oldItems,inputList];    //sprate operator
        });   
        setInputList("");
    }
    };
    
    const deleteItems = (id) =>{
        setItems((oldItems)=>{
            return oldItems.filter((arrElem , index) => {
                return index  !== id;
            });
        });
    };

    const DeleteAll = () =>{
        setItems([])
    }
    

    return(
        <>
            <div className="main_div">
              <div className="center_div">
                <br/>
                <h1> ToDoList</h1>
                <br/>
                <input type="text" placeholder="Add a Items" className="field" onChange={itemEvent} value={inputList} />
                <button onClick={listOfItems} className="Add">Add</button>
                <button className="Add" onClick={DeleteAll}>Delete</button>

                <ol>
                   <li>
                   {
                          Items.map((itemVal , index) =>{
                              return(
                                <div className="div">
           <button className="Delete"   ><DeleteIcon/>
                 </button>
              <button className="Edit"> E</button>
               <h2>Apple</h2>
       </div>
                              )
                          } )
                   }
                    </li>
                </ol>
                
              </div>
            </div>
        </>
    )
    
};
export default App;
