import React, {useState} from "react";
import './index.css';

const App = () =>{

    const [name , setfruits] = usestate("");

    const onClick = (object) =>{
        setfruits(object.target.value);
    };

    const submit = () =>{
        setfruits(name);
    }

    return(
    <>
        <div className="main_div">
                <div className="center_div">
                    <h1>ToDo List Example</h1>
                    <input type='text' placeholder="Add New Items" className="field" onChange={onClick}/>
                    <button onClick={submit}>+</button>
                 
                 <ol>
                     <li>Buy Tshirt</li>
                     <li>Buy Jeans</li>
                 </ol>

                </div>
        </div>
    </>
    )
}; 

export default App;
