import React, { useState } from 'react';

const IncreDecre = () =>{

    const [count , setCount] = useState(0);

    const IncNum = () =>{
        setCount(count + 1);
    };

    const DecNum = () =>{

        if(count>0){
            setCount(count - 1);
        }
        else{
            alert('limited Reached');
        }
    };


    return(
        <>
        <div className='div'>
        <div className='div1'>
            <h1>{count}</h1>
            <button onClick={IncNum}>Increase</button> 
            <button onClick={DecNum} >Decrease</button>
        </div>
        </div>
        </>
    )

}

export default IncreDecre;
